package com.nonITbatch;

import java.util.Iterator;

public class factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=5,i,fact=1;
		
		for( i=1;i<=number;i++) {
			fact=fact*i;
			
		}
		
			
			
			
		System.out.println("factorial="+number+"is"+fact); 
	
	}
		
		
	}


