package com.thekiranacademy.methods;

public class MathDemo {
	 
	void disp()
	{
		System.out.println("absolute of a no="+Math.abs(-98.54485));
		System.out.println("ceil="+Math.ceil(25.45));
		System.out.println("floor="+Math.floor(54.47));
		System.out.println("sqrt="+Math.sqrt(16));
		System.out.println("power="+Math.pow(5,2));
		System.out.println("max num="+Math.max(25, 125));
		
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         MathDemo md=new MathDemo();
        md.disp();

	}

}
